import React, { useState } from "react";

function App() {
  const [messages, setMessages] = useState([
    { text: "Start the conversation!", sender: "bot" },
  ]);
  const [input, setInput] = useState("");

  // Function to handle sending a message
  const sendMessage = () => {
    if (!input.trim()) return;

    // Add user message to chat
    const userMessage = { text: input, sender: "user" };
    setMessages((prevMessages) => [...prevMessages, userMessage]);

    // Simulate bot response (echo with a delay)
    setTimeout(() => {
      const botMessage = {
        text: `You said: "${input}"`,
        sender: "bot",
      };
      setMessages((prevMessages) => [...prevMessages, botMessage]);
    }, 1000);

    setInput("");
  };

  // Handle Enter key press to send message
  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      sendMessage();
    }
  };

  // Feedback button click handler (you can extend this)
  const handleFeedback = () => {
    alert("Thank you for your feedback!");
  };

  return (
    <div style={{ maxWidth: "600px", margin: "auto", padding: "1rem" }}>
      <header
        style={{
          borderBottom: "1px solid #ddd",
          paddingBottom: "1rem",
          marginBottom: "1rem",
          textAlign: "center",
        }}
      >
        <h1>Simple Chatbot</h1>
        <button onClick={handleFeedback}>Feedback</button>
      </header>
      <main
        style={{
          minHeight: "300px",
          border: "1px solid #ddd",
          borderRadius: "4px",
          padding: "1rem",
          marginBottom: "1rem",
          backgroundColor: "#fff",
          overflowY: "auto",
        }}
      >
        {messages.map((msg, index) => (
          <p
            key={index}
            style={{
              textAlign: msg.sender === "user" ? "right" : "left",
              backgroundColor: msg.sender === "user" ? "#DCF8C6" : "#eee",
              borderRadius: "10px",
              padding: "0.5rem 1rem",
              maxWidth: "70%",
              margin: "0.2rem auto",
            }}
          >
            {msg.text}
          </p>
        ))}
      </main>
      <footer
        style={{
          display: "flex",
          justifyContent: "flex-start",
          gap: "0.5rem",
        }}
      >
        <input
          type="text"
          placeholder="Enter your message"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          style={{
            flexGrow: 1,
            borderRadius: "20px",
            border: "1px solid #ccc",
            padding: "0.5rem 1rem",
            fontSize: "1rem",
          }}
        />
        <button
          onClick={sendMessage}
          disabled={!input.trim()}
          style={{
            borderRadius: "20px",
            border: "1px solid #ccc",
            padding: "0.5rem 1rem",
            fontSize: "1rem",
            opacity: input.trim() ? 1 : 0.5,
            cursor: input.trim() ? "pointer" : "default",
          }}
        >
          Send
        </button>
      </footer>
    </div>
  );
}

export default App;
