import React, { useState, useRef, useEffect } from "react";
import chatService from './services/callChatCompletion';
import ChatHistoryArea from './components/chatarea';
import './css/App.css';

function App() {
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);
  const textareaRef = useRef(null);

  const handleSend = async () => {
    if (!message.trim() || loading) return;

    const newUserMessage = { role: "user", content: message.trim() };
    setChatHistory(prevHistory => [...prevHistory, newUserMessage]);
    setMessage(""); // Clear input immediately
    setLoading(true);

    try {
      const currentChatHistory = [...chatHistory, newUserMessage];
      const res = await chatService(message.trim(), currentChatHistory);

      const newAssistantMessage = { 
        role: "assistant", 
        content: res.answer || "Sorry, I couldn't get a response. Please try again." 
      };

      setChatHistory(prevHistory => [...prevHistory, newAssistantMessage]);
    } catch (err) {
      console.error("Failed to get response", err);
      const errorMessage = { 
        role: "assistant", 
        content: "Oops! Something went wrong. Please try again." 
      };
      setChatHistory(prevHistory => [...prevHistory, errorMessage]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // Auto-resize the textarea
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto';
      textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
    }
  }, [message]);

  const handleFeedback = () => {
    alert("Thank you for your feedback!");
  };

  return (
    <div className="chat-container">
      <div className="chat-header">
        <h1>Simple Chatbot</h1>
        <button className="feedback-btn" onClick={handleFeedback}>
          Feedback
        </button>
      </div>
      <ChatHistoryArea chatHistory={chatHistory} loading={loading} />
      <div className="input-group">
        <textarea
          ref={textareaRef}
          className="form-control"
          placeholder="Enter your message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === 'Enter' && !e.shiftKey) {
              e.preventDefault(); // Prevents new line on Enter
              handleSend();
            }
          }}
          disabled={loading}
          rows="1"
        />
        <button 
          className="btn btn-primary" 
          onClick={handleSend} 
          disabled={loading || !message.trim()}>
          {loading ? "Sending..." : "Send"}
        </button>
      </div>
    </div>
  );
}

export default App;
