import React, { useState } from 'react';
import ChatBot from 'react-chatbotify';
import 'react-chatbotify/features/main/chat.css';

function App() {
  const [loading, setLoading] = useState(false);

  // Define the flow of your chatbot conversation
  const flow = {
    start: {
      message: "Hello! What can I help you with today?",
      transition: { duration: 1000 },
      path: "help_options"
    },
    help_options: {
      message: "You can ask me about...",
      options: [
        {
          value: "product",
          label: "Our Products",
          path: "product_info"
        },
        {
          value: "support",
          label: "Technical Support",
          path: "support_info"
        },
        {
          value: "general",
          label: "General Inquiry",
          path: "general_inquiry"
        }
      ]
    },
    product_info: {
      message: "We have a wide range of products. What are you looking for?",
      path: "end"
    },
    support_info: {
      message: "Please describe your technical issue.",
      path: "end"
    },
    general_inquiry: {
      message: "Feel free to ask me anything!",
      path: "end"
    },
    end: {
      message: "Is there anything else I can help you with?",
      options: [
        {
          value: "yes",
          label: "Yes",
          path: "help_options"
        },
        {
          value: "no",
          label: "No",
          path: "farewell"
        }
      ]
    },
    farewell: {
      message: "Have a great day!",
      path: "start"
    }
  };

  return (
    <div style={{ height: "100vh", display: "flex", justifyContent: "center", alignItems: "center" }}>
      <div style={{ width: "100%", maxWidth: "600px", height: "100%", maxHeight: "800px", boxShadow: "0 4px 12px rgba(0,0,0,0.1)", borderRadius: "8px", overflow: "hidden" }}>
        <ChatBot
          flow={flow}
          headerText="Simple Chatbot"
          loadingText="Typing..."
          placeholder="Type your message here..."
          options={{
            chatHistory: {
              storageKey: 'chat_history' // Saves chat history in local storage
            },
            theme: {
              primaryColor: '#007bff',
              secondaryColor: '#e9ecef',
              fontFamily: 'Segoe UI, Tahoma, Geneva, Verdana, sans-serif'
            }
          }}
        />
      </div>
    </div>
  );
}

export default App;
