import React, { useState } from "react";

export default function SimpleChatbot() {
  const [messages, setMessages] = useState([
    { from: "bot", text: "Start the conversation!" },
  ]);
  const [input, setInput] = useState("");

  // Simple bot response logic
  const getBotResponse = (message) => {
    if (!message) return "Please type something!";
    if (message.toLowerCase().includes("hello")) return "Hi there!";
    if (message.toLowerCase().includes("how are you"))
      return "I'm just a bot, but I'm good!";
    if (message.toLowerCase().includes("bye")) return "Goodbye!";
    return "You said: " + message;
  };

  const sendMessage = () => {
    if (!input.trim()) return;
    const userMessage = { from: "user", text: input };
    setMessages((prev) => [...prev, userMessage]);
    const botMessage = { from: "bot", text: getBotResponse(input) };
    setMessages((prev) => [...prev, userMessage, botMessage]);
    setInput("");
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") sendMessage();
  };

  const handleFeedback = () => {
    alert("Thank you for your feedback!");
  };

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1>Simple Chatbot</h1>
        <button onClick={handleFeedback}>Feedback</button>
      </header>

      <main style={styles.chatArea}>
        {messages.map((msg, i) => (
          <div
            key={i}
            style={{
              ...styles.message,
              alignSelf: msg.from === "bot" ? "flex-start" : "flex-end",
              backgroundColor: msg.from === "bot" ? "#eee" : "#85C1E9",
              color: msg.from === "bot" ? "#000" : "#fff",
            }}
          >
            {msg.text}
          </div>
        ))}
      </main>

      <footer style={styles.inputArea}>
        <textarea
          placeholder="Enter your message"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          style={styles.textarea}
          rows={1}
        />
        <button
          onClick={sendMessage}
          disabled={!input.trim()}
          style={styles.sendButton}
        >
          Send
        </button>
      </footer>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: 800,
    height: 600,
    margin: "40px auto",
    display: "flex",
    flexDirection: "column",
    border: "1px solid #ddd",
    borderRadius: 8,
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  header: {
    padding: 20,
    borderBottom: "1px solid #ddd",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  chatArea: {
    flex: 1,
    padding: 20,
    display: "flex",
    flexDirection: "column",
    gap: 10,
    overflowY: "auto",
    backgroundColor: "#fafafa",
  },
  message: {
    maxWidth: "60%",
    padding: 10,
    borderRadius: 12,
  },
  inputArea: {
    borderTop: "1px solid #ddd",
    display: "flex",
    padding: 10,
    gap: 10,
    alignItems: "center",
  },
  textarea: {
    flex: 1,
    resize: "none",
    borderRadius: 20,
    border: "1px solid #ccc",
    padding: 10,
    fontSize: 14,
    fontFamily: "monospace",
  },
  sendButton: {
    padding: "8px 16px",
    borderRadius: 20,
    border: "1px solid #ccc",
    backgroundColor: "#fff",
    cursor: "pointer",
    fontSize: 14,
    color: "#999",
  },
};
