import React, { useState } from "react";
import chatService from './service/chat';
import ChatHistoryArea from './components/chatarea';
import './css/App.css';


function App() {
  const [message, setMessage] = useState("");
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);

  const handleSend = async () => {
    setLoading(true);
    setResponse(null);
    chatHistory.push({ role: "user", content: message });
    setChatHistory([...chatHistory]);
    try {
      const res = await chatService(message, chatHistory);
      console.log('Response from chat service:', res);
      setResponse(res);
      chatHistory.push({ role: "assistant", content: res.answer || res.error });
      setChatHistory([...chatHistory]);
      setMessage(""); // Clear input after sending
    } catch (err) {
      setResponse({ error: "Failed to get response" });
    }
    setLoading(false);
  };

  return (
    <div className="chat-container">
       <ChatHistoryArea chatHistory={chatHistory} />
      <div class="input-group mb-3 fixed-bottom">

        <input
          type="text"
          class="form-control"
          placeholder="Enter your message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />

        <button class="btn btn-primary me-md-2" onClick={handleSend} disabled={loading}>
          {loading ? "Sending..." : "Send Message"}
        </button>
      </div>

    </div>

  );
}

export default App;
